  ...
}

bool dispatch_command(THD *thd, const COM_DATA *com_data,
                      enum enum_server_command command) {
  ...
  thd->set_command(command);
  ...
  switch (command) {
    ...
    case COM_QUERY: {...}
    ...
  }
}
```

